import React,{useState} from 'react';
import Nav from './Nav';

const App = () =>{

    const [light,dark] = useState({
        pageBackground:'backgroundLight'
    })

    function onSwitch(){
        if(light.pageBackground === 'backgroundLight'){
        dark({
            pageBackground:'backgroundDark'
        })}
        else{
        dark(prevValue => {
            return {
                ...prevValue
            }
        })    
        }
    }

    return(
    <main className={light.pageBackground}>
        <Nav onSwitch={onSwitch}/>
    </main>
    )
}

export default App;
